
var LoanStatus = function () {
    var self = this;
    self.waiting_verify = "waiting_verify";   // description:"等待审核"
    self.verify_fail = "verify_fail";         // description:"审核未通过"
    self.raising = "raising";                 // description:"筹款中"
    self.recheck = "recheck";                 // description:"等待复核"
    self.cancel = "cancel";                   // description:"流标"
    self.repaying = "repaying";               // description:"还款中"
    self.repaying_back = "repaying_back";     // description:"结算中"
    self.overdue = "overdue";                 //description:"逾期"
    self.complete = "complete";               // description:"完成"
    self.bad_debt = "bad_debt";               // description:"坏账"
};


module.exports = new LoanStatus();