UserService.prototype.modifyLoginPassword = function (mobile, password, cb) {
    console.log("开始更改密码---------------------------------------")
    console.log("手机号码为：" + mobile);
    console.log("密码为：" + password);
    var conn = dc.main.getConn();
    var tag = false;
    var sql = "SELECT COUNT(*) count FROM user WHERE mobile=" + mobile + " ";
    var sql2 = "UPDATE user SET password = '" + password +"' where mobile = '" + mobile +"' ";
    conn.execute(sql, [], function (err, data) {
        if (err) {
            cb(err, null);
        } else { console.log("值为：" + data[0].count);
            console.log(data);
            if(data[0].count == 1) {
                conn.execute(sql2, [], function (err, data) {
                    if (err) {console.log("sql执行错误");
                        cb(err, null);
                    } else {
                        console.log("长度：" + data.affectedRows);
                        if (data.affectedRows == 1) {
                            tag = true;
                        }
                        cb(err, tag);
                    }
                })
            }else {
                cb(err, null);
            }
        }
    })
};

Date.prototype.Format = function (fmt) { //author: meizz
    var o = {
        "M+": this.getMonth() + 1, //月份
        "d+": this.getDate(), //日
        "H+": this.getHours(), //小时
        "m+": this.getMinutes(), //分
        "s+": this.getSeconds(), //秒
        "q+": Math.floor((this.getMonth() + 3) / 3), //季度
        "S": this.getMilliseconds() //毫秒
    };
    if (/(y+)/.test(fmt)) fmt = fmt.replace(RegExp.$1, (this.getFullYear() + "").substr(4 - RegExp.$1.length));
    for (var k in o)
        if (new RegExp("(" + k + ")").test(fmt)) fmt = fmt.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));
    return fmt;
}
